mapper.select:
select gallery_id
from gallery
where gallery_mainWorkId = #{gallery_mainWorkId}

mapper.update:
update gallery
set gallery_rating = 19
where gallery_id = #{gallery_id}

UPDATE gallery
SET gallery_name = #{gallery_name},
    gallery_desc = #{gallery_desc},
    gallery_mainWorkId = #{gallery_mainWorkId}
WHERE gallery_id = #{gallery_id}
